<style scoped lang="scss">

// chef wizard
.chef-wizard {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    overflow: visible;
    width: fit-content;
    // outline: 1px solid red;

    // title svg
    .title-svg {
        color: var(--black-to-white);
        width: 15rem;
        margin-bottom: 0.5rem;
    }    

    // wizard and tacos container
    &__wizard-and-tacos {
        position: relative;
        // outline: 1px solid red;

        // taco image outer
        .taco-img__outer {
            position: absolute;

            &--delay-1 {
                animation-delay: -2s;
            }

            &--delay-2 {
                animation-delay: -4s;
            }

        }

        // taco dao taco simple image
        .taco-img__inner {
            width: 3rem;
            height: auto;

            &--delay-1 {
                animation-delay: -2s;
            }

            &--delay-2 {
                animation-delay: -4s;
            }

        }

        // chef wizard image
        .chef-wizard-img {
            width: 12rem;
            height: auto;
        }

    }

}

// animations
.float {
    animation: float 6s infinite linear;
}
.bob {
    animation: bob 6s infinite ease-in-out;
}
@keyframes float {
    0% {
        opacity: 0;
        transform: translateX(-3rem) rotate(-30deg);
    }
    15% {
        opacity: 0;
    }
    25% {
        opacity: 1;
    }
    50% {
        opacity: 1;
        transform: translateX(4.5rem) rotate(0deg);
    }
    75% {
        opacity: 1;
    }
    85% {
        opacity: 0;
    }
    100% {
        opacity: 0;
        transform: translateX(12rem) rotate(30deg);
    }
}
@keyframes bob {
    0% {
        transform: translateY(0);
    }
    50% {
        transform: translateY(-1rem);
    }
    100% {
        transform: translateY(0);
    }
}

</style>

