  }
  100% {
    transform: translateY(0); /* Return to starting position */
  }
}

@keyframes turnTacosClockwise {
  0% {
    transform: translateY(0rem) rotate(0);
    opacity: 0.5;
  }
  100% {
    transform: translateY(-15rem) rotate(360deg);
    opacity: 0;
  }
}

@keyframes turnTacosCounterClockwise {
  0% {
    transform: translateY(0rem) rotate(0);
    opacity: 0.5;
  }
  100% {
    transform: translateY(-15rem) rotate(-360deg);
    opacity: 0;
  }
