  /////////////////////
  // Local Variables //
  /////////////////////

  // Track intersection state
  const isBelowTheFoldVisible = ref(false)

  // mobile check
  const isMobile = ref(false)

  // viewing chart modal
  const viewingChartModal = ref(false)

  // images
  const astronautLoaderUrl =  astronautLoader 

  //////////////
  // Computed //
  //////////////

  // date based emojis
  const floatingEmoji1 = computed(() => {

    // get today's date
    const today = new Date()

    // get the month
    const month = today.getMonth() + 1

    // get the day
    const day = today.getDate()
    
    // halloween: October 31st
    if (month === 10 && day === 31) {
      return '🎃'
    }
    
    // christmas: December 25th
    if (month === 12 && day === 25) {
      return '🎅'
    }
    
    // new years: January 1st
    if (month === 1 && day === 1) {
      return '🎉'
    }
    
    // valentines day: February 14th
    if (month === 2 && day === 14) {
      return '❤️'
    }
    
    // st. patrick's day: March 17th
    if (month === 3 && day === 17) {
      return '☘️'
    }
    
    // easter: April 9th (you can adjust this date)
    if (month === 4 && day === 9) {
      return '🐰'
    }
    
    // independence day: July 4th
    if (month === 7 && day === 4) {
      return '💥'
    }

    // cinco de mayo: May 5th
    if (month === 5 && day === 5) {
      return '🇲🇽'
    }

    // 420: April 20th
    if (month === 4 && day === 20) {
      return '🌿'
    }
    
    // default: taco
    return '🌮'

  })
  const floatingEmoji2 = computed(() => {
    const today = new Date()
    const month = today.getMonth() + 1 // getMonth() returns 0-11
    const day = today.getDate()
    
    // halloween: October 31st
    if (month === 10 && day === 31) {
      return '🎃'
    }
    
    // christmas: December 25th
    if (month === 12 && day === 25) {
      return '🎄'
    }
    
    // new years: January 1st
    if (month === 1 && day === 1) {
      return '🍾'
    }
    
    // valentines day: February 14th
    if (month === 2 && day === 14) {
      return '❤️'
    }
    
    // st. patrick's day: March 17th
    if (month === 3 && day === 17) {
      return '☘️'
    }
    
    // easter: April 9th (you can adjust this date)
    if (month === 4 && day === 9) {
      return '🥚'
    }
    
    // independence day: July 4th
    if (month === 7 && day === 4) {
      return '🇺🇸'
    }

    // cinco de mayo: May 5th
    if (month === 5 && day === 5) {
      return '🌮'
    }

    // 420: April 20th
    if (month === 4 && day === 20) {
      return '🌿'
    }
    
    // default: taco
    return '🌮'

  })

  // format number
  const formatNumber = computed(() => {
    return (num) => {
      if (typeof num !== 'number') {
        return num
      }
      if (num >= 10000000) {
        return '10m+'
      }
      if (num >= 1000) {
        return new Intl.NumberFormat('en-US', {
            notation: 'compact',
            compactDisplay: 'short'
        }).format(num).toLowerCase();
      }
      return num.toFixed(2)
    }
  })  

  ///////////////////
  // Local Methods //
  ///////////////////

  //////////////
  // handlers //

  // handle intersection changes
  const handleIntersection = (entries: IntersectionObserverEntry[]) => {

    // loop through entries
    entries.forEach(entry => {

      // set is below the fold visible
      isBelowTheFoldVisible.value = entry.isIntersecting
      
      // log
      // console.log('.home-view__below-the-fold is visible:', entry.isIntersecting)

    })
  }  

  // redirect to dao page
  const redirectToDao = () => {
    window.location.href = '/dao'
  }

  /////////////////////
  // Lifecycle Hooks //
  /////////////////////

  // on mounted
  onMounted(async () => {
    
    // log
    // console.log('homepage mounted')

    // get the below the fold element
    const belowTheFold = document.querySelector('.home-view__below-the-fold')

    // check for mobile
    isMobile.value = /Mobi|Android|iPhone|iPad|iPod/i.test(navigator.userAgent)
    
    // if below the fold exists
    if (belowTheFold) {
      
      // create intersection observer
      const observer = new IntersectionObserver(handleIntersection, {

        // set threshold
        threshold: 0.01

      })

      // sart observing below the fold
      observer.observe(belowTheFold)
      
      // store observer for cleanup
      ;(window as any).belowTheFoldObserver = observer

    } 
    
    // else no below the fold element
    else {

      // log error
      console.error('Could not find below the fold')

    }

    // fetch token details from dao backend
    await fetchTokenDetails()

    // fetch total treasury value in usd
    await fetchTotalTreasuryValueInUsd()    
    
  })

  // clean up observer
  onUnmounted(() => {

    // get the observer
    const observer = (window as any).belowTheFoldObserver
    
    // if observer exists
    if (observer) {

      // stop observing
      observer.disconnect()
      
      // log
      console.log('Intersection observer disconnected')
      
      // clean up
      delete (window as any).belowTheFoldObserver

    }

  })

</script>